'''슬라이드 복사 함수'''

load_prs=Presentation('C:\\pptx_테스트_파일들\\pptx2\\2022_KOREG_IOKI_전략_제안서_v0.1.pptx')
def duplicate_slide(prs, index):
    # 원본 슬라이드 번호 정의
    source_slide = prs.slides[index]
    # 빈 레이아웃 설정 (슬라이드 레이아웃 정의 필요)
    try:
        slide_layout = prs.slide_layouts[6]
    except:
        slide_layout = prs.slide_layouts[len(prs.slide_layouts)]
    copied_slide = prs.slides.add_slide(slide_layout)
    # 원본 슬라이드에서 shape 복제
    for shape in source_slide.shapes:
        el = shape.element
        newel = copy.deepcopy(el)
        copied_slide.shapes._spTree.insert_element_before(newel, 'p:')
    return copied_slide
